/** @format */

// src/components/LeftSide.js

import React, { useState } from "react";
import "./LeftSide.css"; // Import the CSS file from the same folder
import Modal from "./Modal.js";
import useApi from "../useApi";

const LeftSide = () => {
  const [showModal, setShowModal] = useState(false);
  const [selectedCity, setSelectedCity] = useState("");
  const [startDate, setStartDate] = useState(null);
  const [endDate, setEndDate] = useState(null);
  const [trips, setTrips] = useState([
    {
      city: "Beijing",
      date: "Aug 15 - Aug 20",
      image: process.env.PUBLIC_URL + "/images/" + "beijing" + ".jpeg",
      // Replace with actual image URL
    },
  ]);
  // Define the function
  const addTrip = (selectedCity, startDate, endDate) => {
    // Check if the input is not null or ""
    if (selectedCity && startDate && endDate) {
      // Create a new trip object with the input values
      const newTrip = {
        city: selectedCity,
        date: `${startDate} - ${endDate}`,
        image:
          process.env.PUBLIC_URL +
          "/images/" +
          selectedCity.toLowerCase() +
          ".jpeg",
      };

      setTrips((prevTrips) => [...prevTrips, newTrip]);
    }
  };
  const apiKey = "45XHHL2JKQC3XPVRRYAVH2AGS"; // Replace with your API key
  const apiUrl =
    "https://weather.visualcrossing.com/VisualCrossingWebServices/rest/services/timeline/";

  // const [city, setCity] = useState("London");
  // const [startDate, setStartDate] = useState(
  //   new Date().toISOString().substr(0, 10)
  // );
  // const endDate = new Date(
  //   new Date(startDate).getTime() + 6 * 24 * 60 * 60 * 1000
  // )
  //   .toISOString()
  //   .substr(0, 10);

  const fullUrl = `${apiUrl}${selectedCity}/${startDate}/${endDate}?unitGroup=metric&include=days&key=${apiKey}&contentType=json`;

  const { data, loading, error } = useApi(fullUrl);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  }
  console.log(data);

  // Added click event handler for the "Add Trip" button
  const handleModalOpen = () => {
    setShowModal(true);
  };

  // Added click event handler for closing the modal
  const handleModalClose = () => {
    setShowModal(false);
  };

  const weatherForecast = [
    {
      day: "Monday",
      weather: "Sunny",
      temperature: "28˚/21˚",
    },
    // Add six more weather forecast objects for each day
  ];

  console.log(selectedCity);
  console.log(startDate);
  console.log(endDate);

  return (
    <div className='left-side'>
      <div className='top-left'>
        {/* Add any content for the top left corner */}
        <h1>
          Weather <b>Trip</b>
        </h1>
      </div>
      <div className='search-box'>
        <input type='text' placeholder='Search your trip' />
      </div>
      <div className='trip-list'>
        {trips.map((trip, index) => (
          <div className='trip-card' key={index}>
            <img src={trip.image} alt={trip.city} />
            <div className='destination'>{trip.city}</div>
            <div className='date'>{trip.date}</div>
          </div>
        ))}
        {/* Add a card for adding a new trip */}
        <div className='trip-card add-card' onClick={handleModalOpen}>
          {/* Add icon or image for adding a new trip */}
          <div className='add-icon'>+</div>

          <div className='destination'>Add Trip</div>
        </div>
        <Modal
          isOpen={showModal}
          onClose={handleModalClose}
          selectedCity={selectedCity}
          setSelectedCity={setSelectedCity}
          startDate={startDate}
          setStartDate={setStartDate}
          endDate={endDate}
          setEndDate={setEndDate}
          addTrip={addTrip}
        />
      </div>
      <h3>Week</h3>
      <div className='weather-forecast'>
        {weatherForecast.map((weatherData, index) => (
          <div className='weather-card' key={index}>
            <div className='day'>{weatherData.day}</div>
            <div className='weather'>{weatherData.weather}</div>
            <div className='temperature'>{weatherData.temperature}</div>
          </div>
        ))}
      </div>
      {showModal && (
        <div className='modal'>
          <div className='modal-content'>
            {/* Added the close icon */}
            <span className='close' onClick={handleModalClose}>
              &times;
            </span>
            {/* Added the content for the modal */}
            <h2>Add New Trip</h2>
            {/* Add form or input fields for adding a new trip */}
          </div>
        </div>
      )}
    </div>
  );
};

export default LeftSide;
