/** @format */

// src/components/Modal.js

import React, { useState } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import "./Modal.css";

const cities = ["City A", "City B", "City C"]; // Replace this with your list of cities

const Modal = ({
  isOpen,
  onClose,
  selectedCity,
  setSelectedCity,
  startDate,
  setStartDate,
  endDate,
  setEndDate,
}) => {
  const [isCityDropdownOpen, setIsCityDropdownOpen] = useState(false);
  const [isStartDateDropdownOpen, setIsStartDateDropdownOpen] = useState(false);
  const [isEndDateDropdownOpen, setIsEndDateDropdownOpen] = useState(false);

  if (!isOpen) return null;

  const handleCityChange = (city) => {
    setSelectedCity(city);
    setIsCityDropdownOpen(false);
  };


  return (
    <div className='modal-overlay'>
      <div className='modal'>
        <div className='modal-header'>
          <h2>Create Trip</h2>
          <button className='close-btn' onClick={onClose}>
            &times;
          </button>
        </div>
        <hr className='divider' />
        <div className='modal-content'>
          {/* Add form elements here */}
          <form>
            <div className='form-field'>
              <label htmlFor='city'>
                <span className='required'>*</span>City
              </label>
              <div className='input-with-dropdown'>
                <input
                  type='text'
                  id='city'
                  value={selectedCity}
                  placeholder='Please select a city'
                  onClick={() => setIsCityDropdownOpen(!isCityDropdownOpen)}
                  readOnly
                />
                <div
                  className={`dropdown-icon ${
                    isCityDropdownOpen ? "open" : ""
                  }`}
                  onClick={() => setIsCityDropdownOpen(!isCityDropdownOpen)}>
                  ⌵
                </div>
                {isCityDropdownOpen && (
                  <div className='dropdown-list'>
                    {cities.map((city) => (
                      <div
                        key={city}
                        className='dropdown-item'
                        onClick={() => handleCityChange(city)}>
                        {city}
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>
            <div className='form-field'>
              <label htmlFor='start-date'>
                <span className='required'>*</span>Start Date
              </label>
              <div className='input-with-dropdown'>
                <input
                  type='text'
                  id='start-date'
                  value={startDate}
                  placeholder='Please select a start date'
                  onClick={() =>
                    setIsStartDateDropdownOpen(!isStartDateDropdownOpen)
                  }
                />
                <div
                  className={`dropdown-icon ${
                    isStartDateDropdownOpen ? "open" : ""
                  }`}
                  onClick={() =>
                    setIsStartDateDropdownOpen(!isStartDateDropdownOpen)
                  }>
                  🗓️
                </div>
                {isStartDateDropdownOpen && (
                  <div className='dropdown-list'>
                    <DatePicker
                      selected={startDate}
                      onChange={(date) => {
                        setStartDate(date);
                        setIsStartDateDropdownOpen(false);
                      }}
                      minDate={new Date()}
                      maxDate={
                        new Date(
                          new Date().getTime() + 15 * 24 * 60 * 60 * 1000
                        )
                      }
                      dateFormat='yyyy-MM-dd'
                      inline
                    />
                  </div>
                )}
              </div>
            </div>
            <div className='form-field'>
              <label htmlFor='end-date'>
                <span className='required'>*</span>End Date
              </label>
              <div className='input-with-dropdown'>
                <input
                  type='text'
                  id='end-date'
                  value={endDate}
                  placeholder='Please select an end date'
                  onClick={() =>
                    setIsEndDateDropdownOpen(!isEndDateDropdownOpen)
                  }
                />
                <div
                  className={`dropdown-icon ${
                    isStartDateDropdownOpen ? "open" : ""
                  }`}
                  onClick={() =>
                    setIsEndDateDropdownOpen(!isEndDateDropdownOpen)
                  }>
                  🗓️
                </div>
                {isEndDateDropdownOpen && (
                  <div className='dropdown-list'>
                    <DatePicker
                      selected={endDate}
                      onChange={(date) => {
                        setEndDate(date);
                        setIsEndDateDropdownOpen(false);
                      }}
                      minDate={new Date()}
                      maxDate={
                        new Date(
                          new Date().getTime() + 15 * 24 * 60 * 60 * 1000
                        )
                      }
                      dateFormat='yyyy-MM-dd'
                      inline
                    />
                  </div>
                )}
              </div>
            </div>
          </form>
        </div>
        <hr className='divider' />
        <div className='modal-footer'>
          <button className='cancel-btn' onClick={onClose}>
            Cancel
          </button>
          <button className='save-btn' type='submit'>
            Save
          </button>
        </div>
      </div>
    </div>
  );
};

export default Modal;
